mapper.select:
select o.id, o.productId, o.hcId, o.userId, o.isValid, o.createTime, o.createUserId, o.modifyTime, o.modifyUserId from (select row_number() over(order by modifyTime desc) as rownumber, * from ( select * from tb_collection
and hcId=#{hcId}
and userId=#{userId}
) as oo ) as o where rownumber > #{page.rowNumber}

select count(*) from (select * from tb_collection


          and hcId=#{hcId}


          and userId=#{userId}


)t
