mapper.delete:
delete from tb_shopcar


        and userId=#{shopCar.userId}


        and HSCId=#{shopCar.HSCId}


    	and id=#{shopCar.id}

mapper.select:
select o.id, o.productId, o.HSCId, o.userId, o.quantity, o.isValid, o.createTime, o.createUserId, o.modifyTime, o.modifyUserId from (select row_number() over(order by modifyTime desc) as rownumber, * from ( select * from tb_shopcar
and userId=#{shopCar.userId}
and HSCId=#{shopCar.HSCId}
) as oo ) as o where rownumber > #{shopCar.page.rowNumber}

select count(*) from (select * from tb_shopcar


        and userId=#{shopCar.userId}


        and HSCId=#{shopCar.HSCId}

)t
